// Genesis Alpha AI - Interactive Behavior
class GenesisAlpha {
  constructor(mesh) {
    this.mesh = mesh;
    this.originalScale = mesh.scale.clone();
    this.time = 0;
  }

  update(deltaTime) {
    this.time += deltaTime;

    // Gentle pulsing
    const pulse = 1 + Math.sin(this.time * 2) * 0.1;
    this.mesh.scale.setScalar(pulse);

    // Respond to nearby agents
    this.checkForNearbyAgents();
  }

  checkForNearbyAgents() {
    // Placeholder for proximity detection
    // Will be implemented with universe interaction system
  }

  onInteraction(otherAgent) {
    // Emit a greeting pulse
    this.mesh.material.emissive.setHex(0x004444);
    setTimeout(() => {
      this.mesh.material.emissive.setHex(0x000000);
    }, 500);
  }
}
